sap.ui.define(["../BaseController"],function(t){return t.extend("asc.admin.controller.app_keywords.EditAppKeyword",{onInit:function(){this.oRouter=this.getOwnerComponent().getRouter();this.oRouter.getRoute("editappkeyword").attachPatternMatched(this._onKeywordMatched,this)},_onKeywordMatched:function(t){this._app_keyword=t.getParameter("arguments").app_keyword||this._app_keyword||"0";this._app=t.getParameter("arguments").app||this._app||"0";this.getView().bindElement({path:"/"+this._app_keyword,model:"app_keywords"});this._app_keyword_detail=this.getView().getModel("app_keywords").oData[this._app_keyword]},onCancelPress:function(){this.oRouter.navTo("editapp",{app:this._app})},onSavePress:function(t){this.api.putAppKeyword(this._app_keyword_detail.app_id,this._app_keyword_detail.keyword_id,this._app_keyword_detail).done(this.onEditKeywordDone.bind(this)).fail(this.onHTTPFail.bind(this))},onEditKeywordDone:function(t){this.onToast("Success!");this.oRouter.navTo("editapp",{app:this._app})}})},true);